import  React, { useState } from "react";
import {render} from "react-dom";
import Calednar, {activeStartDate} from "react-calendar";
import 'react-calendar/dist/Calendar.css'
import * as moment from "moment"

const ReactCalendar = () =>{
  const [input, setInput] = useState(new Date());
  const [date,setDate] = useState(new Date());
  const OnInput = event => {
    setInput(event.target.value);   
  }
  const OnClick = event => {
    moment(input).format('DD.MM.YYYY');
    setDate(input)
  }
  const onChange = date =>{
    setDate(date)

  }

  return <div>
    <Calednar onChange={onChange} value={date}/>
    {console.log(date)}
    {moment(date).format('DD.MM.YYYY')}
    <input value={input} onChange={OnInput}/>     
    <input type="submit" value="Отправить" onClick={OnClick}/>
  </div>
};



render(<ReactCalendar />, document.querySelector("#root"));
