    setDate(date)
  }
  return <div>
    <Calednar onChange={onChange} value={date} />

    {console.log(moment(values.date).format('YYYY-MM-DD'))}
    {date.toString()}
  </div>
};

render(<ReactCalendar />, document.querySelector("#root"));
