  const [date,setDate] = useState(new Date());
  const onChange = date =>{
    setDate(date)  
    alert(date)    
  }

  return <div>
    <Calednar id="2" onChange={onChange} value={date} selectRange  />
    {console.log(date)}
  </div>  
};




