import  React, { useState } from "react";
import {render} from "react-dom";
import Calednar, {activeStartDate} from "react-calendar";
import 'react-calendar/dist/Calendar.css'
import * as moment from "moment"

const ReactCalendar = () =>{
  const [input_day, setInputDay] = useState('');
  const [input_month, setInputMonth] = useState('');
  const [input_year, setInputYear] = useState('');
  const [date,setDate] = useState(new Date());
  const OnInputDay = event => {
    setInputDay(event.target.value); 
    date.setDate(input_day);  
  }
  const OnInputMonth = event => {
    setInputMonth(event.target.value-1); 
    date.setMonth(input_month);  
  }
  const OnInputYear = event => {
    setInputYear(event.target.value);  
    setDate(date);
  }
  const OnClick = event => {



    date.setFullYear(input_year);

    event.preventDefault();
  }
  const onChange = date =>{
    setDate(date)

  }

  return <div>

    <Calednar onChange={onChange} value={date}/>
    {console.log(date)}
    {moment(date).format('DD.MM.YYYY')}
    <input  onInput={OnInputDay} onChange={OnInputDay}/>
    <input  onInput={OnInputMonth} onChange={OnInputMonth}/>
    <input  onInput={OnInputYear} onChange={OnInputYear}/>     
    <input type="submit" value="Установить" onClick={OnClick}/>
    <input type="submit" value="Установит" onClick={OnInputYear}/>
  </div>
};



render(<ReactCalendar />, document.querySelector("#root"));
