import  React, { useState }  from "react";
import { useRef } from "react";
import {render} from "react-dom";
import Calednar, {activeStartDate} from "react-calendar";
import 'react-calendar/dist/Calendar.css'
import * as moment from "moment"

const ReactCalendar = () =>{
  const [input_day, setInputDay] = useState('');
  const [input_month, setInputMonth] = useState('');
  const [input_year, setInputYear] = useState('');
  const [date,setDate] = useState(new Date());
  const [date1,setDate1] = useState(new Date());
  const calendarOutput = useRef(null).current;
  useRef(null).current = "TEST";
  const OnInputDay = event => {
    setInputDay(event.target.value);   
  }
  const OnInputMonth = event => {
    setInputMonth(event.target.value-1);   
  }
  const OnInputYear = event => {
    setInputYear(event.target.value);  
  }
  const OnClick = event => {

    date1.setDate(input_day);
    date1.setMonth(input_month);
    date1.setFullYear(input_year);
    setDate(date1);
    event.preventDefault();
  }
  const onChange = date =>{
    setDate(date)

  }
  const OnClick1 = event => {
    calendarOutput.selectRange=false;
   }

  return <div>

    <Calednar id="mycalendar" onChange={onChange} value={date} selectRange={true} ref={calendarOutput}/>

    <input type="submit" value="Установить" onClick={OnClick1} />
    {console.log(date)}
    {moment(date).format('DD.MM.YYYY')}
    <div>
      <table>
        <td>
          <tr> <label>
            День
            </label>
            </tr>
            <tr>
            <input  onInput={OnInputDay} onChange={OnInputDay}/>
            </tr>
        </td>
        <td>
        <tr> <label>
          Месяц
          </label>
          </tr>
          <tr>
          <input  onInput={OnInputMonth} onChange={OnInputMonth}/>
          </tr>
        </td>
        <td>
        <tr> <label>
          Год
          </label>
          </tr>
          <tr>
          <input  onInput={OnInputYear} onChange={OnInputYear} />
          </tr>
        </td>
        <td>
          <tr><label> Нажать</label>  </tr>

          <tr>
            <input type="submit" value="Установить" onClick={OnClick} onClickCapture={OnInputYear}/>
          </tr>
        </td>
      </table>
    </div>



  </div>
};
render(<ReactCalendar />, document.querySelector("#root"));
